import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery } from 'react-query';
import { booksAPI } from '../api/booksAPI';
import { Book } from '../types';
import { useCart } from '../contexts/CartContext';
import BookCard from '../components/common/BookCard';
import Loading from '../components/common/Loading';
import toast from 'react-hot-toast';

const BookDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const { addToCart } = useCart();
  const [quantity, setQuantity] = useState(1);

  const { data: book, isLoading: isLoadingBook, error } = useQuery<Book, Error>(
    ['book', id],
    () => booksAPI.getBookById(id!),
    { enabled: !!id }
  );

  const { data: relatedBooksResponse, isLoading: isLoadingRelated } = useQuery(
    ['relatedBooks', book?.category._id],
    () => booksAPI.getBooks(0, 4, { category: book!.category._id }),
    { enabled: !!book }
  );

  const relatedBooks = relatedBooksResponse?.data?.filter(
    (relatedBook) => relatedBook._id !== book?._id
  );

  const handleQuantityChange = (change: number) => {
    if (!book) return;
    const newQuantity = quantity + change;
    if (newQuantity > 0 && newQuantity <= book.quantity) {
      setQuantity(newQuantity);
    }
  };

  const handleAddToCart = () => {
    if (book) {
      addToCart(book, quantity);
      toast.success(`Đã thêm ${quantity} cuốn "${book.title}" vào giỏ hàng`);
    }
  };

  if (isLoadingBook) return <Loading text="Đang tải chi tiết sách..." />;
  if (error || !book) {
    return (
      <div className="text-center py-20">
        <h2 className="text-2xl font-semibold">Không tìm thấy sách</h2>
        <p className="text-gray-600 mt-2">Sản phẩm bạn tìm kiếm không tồn tại hoặc đã bị xóa.</p>
        <Link to="/books" className="btn btn-primary mt-6">
          Quay lại trang sách
        </Link>
      </div>
    );
  }

  return (
    <div className="bg-gray-50 py-8">
      <div className="container mx-auto px-4">
        <div className="bg-white p-8 rounded-lg shadow-md mb-8">
          <div className="grid md:grid-cols-2 gap-8 lg:gap-12">
            {/* Image */}
            <div className="bg-gray-100 rounded-lg flex items-center justify-center p-4">
              <img
                src={book.images[0] || '/placeholder-image.png'}
                alt={book.title}
                className="w-full max-w-sm h-auto object-contain"
                onError={(e) => { e.currentTarget.src = '/placeholder-image.png'; }}
              />
            </div>

            {/* Details */}
            <div>
              <Link to={`/books?category=${book.category._id}`} className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mb-4 hover:bg-gray-300">
                {book.category.name}
              </Link>
              <h1 className="text-3xl lg:text-4xl font-bold text-gray-800 mb-2">{book.title}</h1>
              <p className="text-lg text-gray-600 mb-4">
                Tác giả: <span className="text-indigo-600 font-medium">{book.author}</span>
              </p>
              <p className="text-3xl font-bold text-red-600 mb-6">{book.price.toLocaleString('vi-VN')} ₫</p>

              <div className="prose max-w-none text-gray-600 mb-6">
                <p>{book.description}</p>
              </div>

              <div className="flex items-center space-x-4 mb-6">
                <span className="font-semibold">Số lượng:</span>
                <div className="flex items-center border rounded">
                  <button onClick={() => handleQuantityChange(-1)} className="px-3 py-1">-</button>
                  <span className="px-4 py-1">{quantity}</span>
                  <button onClick={() => handleQuantityChange(1)} className="px-3 py-1">+</button>
                </div>
                <span className="text-gray-500">
                  ({book.quantity} sản phẩm có sẵn)
                </span>
              </div>

              <button 
                onClick={handleAddToCart}
                disabled={book.quantity === 0}
                className="bg-indigo-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-indigo-700 transition duration-300 disabled:opacity-50"
              >
                {book.quantity > 0 ? 'Thêm vào giỏ hàng' : 'Hết hàng'}
              </button>
            </div>
          </div>
        </div>

        {/* Related Books */}
        {relatedBooks && relatedBooks.length > 0 && (
          <div>
            <h2 className="text-2xl font-bold mb-6">Sách liên quan</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {relatedBooks.map((relatedBook) => (
                <BookCard key={relatedBook._id} book={relatedBook} />
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default BookDetailPage;
